import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from "../../navbar/Navbar";
import './CreatePost.css';

const CreatePost: React.FC = () => {
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [link, setLink] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // The expected user ID for posting
    const testUserId = "12345";

    useEffect(() => {
        // Retrieve user data from localStorage
        const loggedUser = localStorage.getItem('user');
        if (loggedUser) {
            const userInfo = JSON.parse(loggedUser);
            // Check if the logged-in user is the test user
            if (userInfo.id !== testUserId) {
                setError('You are not authorized to post.');
            }
        } else {
            setError('You must be logged in to post.');
        }
    }, []);

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        // Re-check user authorization before submitting in case local storage was modified after the component mounted
        const userInfo = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')!) : null;
        if (!userInfo || userInfo.id !== testUserId) {
            setError('You are not authorized to post.');
            return;
        }

        // Ensure all fields are filled
        if (!title || !text || !link) {
            setError('All fields are required');
            return;
        }

        console.log({ title, text, link, userId: userInfo.id });
        setError('');
        navigate('/');
    };

    const handleCancel = () => {
        navigate('/');
    };

    return (
        <div>
            <Navbar />
            <form className="create-post-form" onSubmit={handleSubmit}>
                <label>
                    Title:
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </label>
                <label>
                    Text:
                    <textarea value={text} onChange={(e) => setText(e.target.value)} />
                </label>
                <label>
                    Link:
                    <input type="text" value={link} onChange={(e) => setLink(e.target.value)} />
                </label>
                {error && <p className="error">{error}</p>}
                <div className="create-post-button-container">
                    <button type="submit" className="create-post-button">Create Post</button>
                    <button type="button" className="create-post-button" onClick={handleCancel}>Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default CreatePost;
