import React from 'react';
import './Modal.css'; // Ensure you have appropriate CSS for modal

interface ModalProps {
    isOpen: boolean;
    children: React.ReactNode; // React.ReactNode covers anything that can be rendered: numbers, strings, JSX, arrays of these, etc.
    onClose: () => void; // Function that doesn't take any arguments and doesn't return anything
}

const Modal: React.FC<ModalProps> = ({ isOpen, children, onClose }) => {
    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <button onClick={onClose} className="close-button">X</button>
                {children}
            </div>
        </div>
    );
};

export default Modal;
