import React, { useState, useEffect } from 'react';
import { getCommentsForPost, postComment, CommentData } from '../../../api/api';

interface Post {
    _id: string;
    title: string;
    text: string;
    link?: string;
    userId: string;
    upvotes: number;
    _comments: CommentData[];
    _creator: {
        username: string;
    };
    createdAt: string;
    updatedAt?: string;
    showComments: boolean;
    upvoted?: boolean;
    downvoted?: boolean;
}

interface PostsProps {
    currentFilter: string; // Define the type for the prop
}

const Posts: React.FC<PostsProps> = ({ currentFilter }) => {
    const [posts, setPosts] = useState<Post[]>([]);
    const [localPost, setLocalPost] = useState<Post | null>(null);
    const [newCommentText, setNewCommentText] = useState('');
    const userId = 'user-id'; // Replace with actual user ID

    useEffect(() => {
        // Fetch posts based on the currentFilter and set state logic
        // Example fetch logic (you need to implement the actual API call):
        const fetchPosts = async () => {
            // Assuming you have an API method to fetch posts based on a filter
            const filteredPosts = await fetchPostsByFilter(currentFilter);
            setPosts(filteredPosts);
        };

        fetchPosts();
    }, [currentFilter]); // Re-fetch posts when the filter changes

    const toggleComments = async (postId: string) => {
        const post = posts.find(p => p._id === postId);
        if (!post) return;

        if (!post.showComments && post._comments.length === 0) {
            const comments = await getCommentsForPost(post._id);
            setLocalPost(prevPost => {
                if (!prevPost) return null;
                return { ...prevPost, _comments: comments, showComments: !prevPost.showComments };
            });
        } else {
            setLocalPost(prevPost => {
                if (!prevPost) return null;
                return { ...prevPost, showComments: !prevPost.showComments };
            });
        }
    };

    const handleCommentSubmit = async (postId: string) => {
        if (!localPost || !newCommentText.trim()) return;

        try {
            const newComment = await postComment(newCommentText, postId, userId);
            setLocalPost(prevPost => {
                if (!prevPost) return null;
                return {
                    ...prevPost,
                    _comments: [...prevPost._comments, newComment],
                    showComments: true,
                };
            });

            setPosts(prevPosts => prevPosts.map(p =>
                p._id === postId ? { ...p, _comments: [...p._comments, newComment] } : p
            ));
        } catch (error) {
            console.error('Error posting comment:', error);
        }
    };

    return (
        <div>
            {posts.map(post => (
                <div key={post._id}>
                    <h2>{post.title}</h2>
                    <p>{post.text}</p>
                    <button onClick={() => toggleComments(post._id)}>
                        {post.showComments ? 'Hide Comments' : 'Show Comments'}
                    </button>
                    {post.showComments && (
                        <div>
                            {post._comments.map(comment => (
                                <div key={comment._id}>
                                    <p>{comment.text}</p>
                                    <small>{comment._creator.username}</small>
                                </div>
                            ))}
                            <textarea
                                value={newCommentText}
                                onChange={(e) => setNewCommentText(e.target.value)}
                            />
                            <button onClick={() => handleCommentSubmit(post._id)}>Add Comment</button>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default Posts;
