import React, { useState } from 'react';
import './Filter.css';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import WhatshotIcon from '@mui/icons-material/Whatshot';

type FilterOption = 'newReleases' | 'trending';
type ReleaseType = 'New Releases' | 'Latest Releases';

export default function Filter() {
    const [selectedFilter, setSelectedFilter] = useState<FilterOption>('newReleases');
    const [releaseType, setReleaseType] = useState<ReleaseType>('New Releases');
    const [showDropdown, setShowDropdown] = useState(false);

    const handleFilterClick = (filter: FilterOption) => {
        setSelectedFilter(filter);
    };

    const handleDropdownToggle = (e: React.MouseEvent) => {
        e.stopPropagation();
        setShowDropdown(!showDropdown);
    };

    const selectReleaseType = (type: ReleaseType) => {
        setReleaseType(type);
        setShowDropdown(false);
    };

    return (
        <div className="filter-container">
            <button
                className={`filter-button ${selectedFilter === 'newReleases' ? 'active' : ''}`}
                onClick={() => handleFilterClick('newReleases')}
            >
                <AccessTimeIcon />
                {releaseType}
                <ArrowDropDownIcon onClick={handleDropdownToggle} style={{ cursor: 'pointer', marginLeft: '5px' }} />
                {showDropdown && (
                    <div className="dropdown-menu">
                        <div className="dropdown-item" onClick={() => selectReleaseType('New Releases')}>New Releases</div>
                        <div className="dropdown-item" onClick={() => selectReleaseType('Latest Releases')}>Latest Releases</div>
                    </div>
                )}
            </button>
            <button
                className={`filter-button ${selectedFilter === 'trending' ? 'active' : ''}`}
                onClick={() => handleFilterClick('trending')}
            >
                <WhatshotIcon />
                Trending
            </button>
        </div>
    );
}
