import React, { useEffect, useState } from 'react';
import { fetchPosts } from '../../../api/api';
import './Posts.css';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import Button from '../../button/Button';
import { ModeComment, Share } from "@mui/icons-material";

// Adjust your Post interface to include all necessary properties
interface Post {
    _id: string;
    title: string;
    text: string;
    link?: string;
    userId: string;
    _comments: Comment[]; // Assuming you have a Comment interface
    upvotes: number;
    _creator: { username: string };
    createdAt: string;
    updatedAt?: string;
}

// Assuming PostData is structured as returned by the API
interface PostData {
    _id: string;
    title: string;
    text: string;
    link?: string;
    userId: string;
    _creator: { username: string };
    createdAt: string;
    updatedAt?: string;
}

const Posts: React.FC = () => {
    const [posts, setPosts] = useState<Post[]>([]);

    useEffect(() => {
        const initFetch = async () => {
            try {
                const data: PostData[] = await fetchPosts();
                // Enrich PostData with default values for _comments and upvotes
                const enrichedPosts: Post[] = data.map(post => ({
                    ...post,
                    _comments: [], // Default empty array for comments
                    upvotes: 0, // Default to zero upvotes
                }));
                setPosts(enrichedPosts);
            } catch (error) {
                console.error('Error loading posts:', error);
                setPosts([]);
            }
        };

        initFetch();
    }, []);

    return (
        <div className="posts-wrapper">
            {posts.map((post: Post) => (
                <div key={post._id} className="post">
                    <div className="post-sidebar">
                        <ThumbUpOffAltIcon className="upvote"/>
                        <span>{post.upvotes}</span>
                        <ThumbDownOffAltIcon className="downvote"/>
                    </div>
                    <div className="post-title">
                        <span>Posted by {post._creator.username}</span>
                        <div className="post-title-username">{post.title}</div>
                        <div className="post-title-time">{new Date(post.createdAt).toLocaleString()}</div>
                        <Button label="+Follow" className="follow-button" />
                    </div>
                    <div className="post-body">{post.text}</div>
                    <div className="post-footer">
                        <div className="comments footer-action">
                            <ModeComment />
                            <span>{post._comments.length}</span>
                        </div>
                        <div className="share footer-action">
                            <Share />
                            <span>Share</span>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Posts;
