import React, { useEffect, useState } from 'react';
import { fetchPosts, fetchPostVotes, upvotePost, downvotePost, undoVotePost } from '../../../api/api';
import './Posts.css';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import Button from '../../button/Button';
import { ModeComment, Share } from "@mui/icons-material";
import { Post } from '../../../models/post/PostModel';

interface VoteResponse {
    success: boolean;
    message?: string;
    netVotes?: number;
}

interface PostsProps {
    currentFilter: string;
}

const Posts: React.FC<PostsProps> = ({ currentFilter }) => {
    const [posts, setPosts] = useState<Post[]>([]);
    const userId = 'YOUR_USER_ID';  // This should be dynamically set based on logged-in user info

    useEffect(() => {
        const initFetch = async () => {
            const data = await fetchPosts();
            const postsWithVotes = await Promise.all(data.map(async post => {
                const votesCount = await fetchPostVotes(post._id);
                return {
                    ...post,
                    upvotes: votesCount,
                    upvoted: false,
                    downvoted: false,
                    _comments: []
                };
            }));
            setPosts(postsWithVotes);
        };

        initFetch();
    }, [currentFilter]);

    const handleVote = async (postId: string, type: 'upvote' | 'downvote' | 'undo') => {
        const postIndex = posts.findIndex(post => post._id === postId);
        if (postIndex === -1) return;

        let updatedPosts = [...posts];
        let currentPost = updatedPosts[postIndex];

        let response: VoteResponse = { success: false, message: "Invalid vote operation" };

        if (type === 'upvote' && !currentPost.upvoted) {
            response = await upvotePost({ postId, userId });
        } else if (type === 'downvote' && !currentPost.downvoted) {
            response = await downvotePost({ postId, userId });
        } else if (type === 'undo') {
            response = await undoVotePost({ postId, userId });
        }

        if (response.success) {
            if (type === 'upvote' && !currentPost.upvoted) {
                currentPost.upvotes += currentPost.downvoted ? 2 : 1;
                currentPost.upvoted = true;
                currentPost.downvoted = false;
            } else if (type === 'downvote' && !currentPost.downvoted) {
                currentPost.upvotes -= currentPost.upvoted ? 2 : 1;
                currentPost.downvoted = true;
                currentPost.upvoted = false;
            } else if (type === 'undo') {
                if (currentPost.upvoted) currentPost.upvotes -= 1;
                if (currentPost.downvoted) currentPost.upvotes += 1;
                currentPost.upvoted = false;
                currentPost.downvoted = false;
            }
            updatedPosts[postIndex] = currentPost;
            setPosts(updatedPosts);
        } else {
            console.error(response.message);
        }
    };

    return (
        <div className="posts-wrapper">
            {posts.map(post => (
                <div key={post._id} className="post">
                    <div className="post-sidebar">
                        <ThumbUpOffAltIcon className="upvote" onClick={() => handleVote(post._id, post.upvoted ? 'undo' : 'upvote')} />
                        <span>{post.upvotes}</span>
                        <ThumbDownOffAltIcon className="downvote" onClick={() => handleVote(post._id, post.downvoted ? 'undo' : 'downvote')} />
                    </div>
                    <div className="post-title">
                        <span>Posted by {post._creator.username}</span>
                        <div className="post-title-username">{post.title}</div>
                        <div className="post-title-time">{new Date(post.createdAt).toLocaleString()}</div>
                        <Button label="+Follow" className="follow-button" />
                    </div>
                    <div className="post-body">{post.text}</div>
                    <div className="post-footer">
                        <ModeComment />
                        <span>{post._comments.length} Comments</span>
                        <Share />
                        <span>Share</span>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Posts;
