import React, { useState } from 'react';
import './Actions.css';
import Button from "../../button/Button"; // Make sure the path is correct
import PersonIcon from '@mui/icons-material/Person';
import { ArrowDropDown } from "@mui/icons-material";

// Define a test user data
const TEST_USER = {
    id: "12345",
    username: "testuser",
    email: "testuser@example.com"
};

export default function Actions() {
    // State to track whether the user is logged in
    const [user, setUser] = useState(null);

    // Function to simulate user login
    const handleLogin = () => {
        setUser(TEST_USER); // Set the user state to the test user
        localStorage.setItem('user', JSON.stringify(TEST_USER)); // Storing user info in localStorage for persistence
        console.log('Logged in as Test User:', TEST_USER);
        // Add any redirection or additional actions needed after login
    };

    // Function to simulate user logout
    const handleLogout = () => {
        setUser(null); // Clear the user state
        localStorage.removeItem('user'); // Clear the user info from localStorage
        console.log('Logged out');
    };

    return (
        <div className="actions">
            {!user && <Button label="Log In" onClick={handleLogin} />} {/* Show login button only if user is not logged in */}
            {user && (
                <div className="profile">
                    <PersonIcon className="hoverable" />
                    <span>{user.username}</span> {/* Display the username */}
                    <ArrowDropDown className="hoverable" />
                    <Button label="Log Out" onClick={handleLogout} /> {/* Add logout button */}
                </div>
            )}
        </div>
    );
}
