import React, { useState } from 'react';
import './Filter.css';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import WhatShotIcon from '@mui/icons-material/Whatshot';

type FilterOption = 'newReleases' | 'trending'; // Add this line

export default function Filter() {
    const [selectedFilter, setSelectedFilter] = useState<FilterOption>('newReleases'); // Specify the type for state

    const handleFilterClick = (filter: FilterOption) => { // Specify the type for parameter
        setSelectedFilter(filter);
    };

    return (
        <div className="filter-container">
            <button
                className={`filter-button ${selectedFilter === 'newReleases' ? 'active' : ''}`}
                onClick={() => handleFilterClick('newReleases')}
            >
                <AccessTimeIcon />
                New Releases
            </button>
            <button
                className={`filter-button ${selectedFilter === 'trending' ? 'active' : ''}`}
                onClick={() => handleFilterClick('trending')}
            >
                <WhatShotIcon />
                Trending
            </button>
        </div>
    );
}
