import React, { useState } from 'react';
import './Actions.css';
import Button from "../../button/Button";
import PersonIcon from '@mui/icons-material/Person';
import { ArrowDropDown } from "@mui/icons-material";
import Modal from '../Modal'; // Import the Modal component

export default function Actions() {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [form, setForm] = useState({
        name: '',
        email: '',
        password: ''
    });

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting', form);
        // Here you would call your userController to handle registration
        setIsModalOpen(false); // Close modal after submit
    };

    return (
        <div className="actions">
            <Button label="Log In" onClick={() => setIsModalOpen(true)}/>
            <Button primary label="Sign Up" onClick={() => setIsModalOpen(true)}/>
            <div className="profile">
                <PersonIcon className="hoverable"/>
                <ArrowDropDown className="hoverable"/>
            </div>

            <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
                <form onSubmit={handleSubmit}>
                    <label>
                        Name:
                        <input type="text" name="name" value={form.name} onChange={handleChange} required />
                    </label>
                    <label>
                        Email:
                        <input type="email" name="email" value={form.email} onChange={handleChange} required />
                    </label>
                    <label>
                        Password:
                        <input type="password" name="password" value={form.password} onChange={handleChange} required />
                    </label>
                    <button type="submit">Register</button>
                </form>
            </Modal>
        </div>
    );
}
