import React, { useEffect, useState } from 'react';
import { fetchPosts, fetchPostVotes, upvotePost, downvotePost, undoVotePost } from '../../../api/api';
import './Posts.css';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import Button from '../../button/Button';
import { ModeComment, Share } from "@mui/icons-material";
import { Post as PostModel } from '../../../models/post/PostModel';

const Posts: React.FC = () => {
    const [posts, setPosts] = useState<PostModel[]>([]);

    useEffect(() => {
        const initFetch = async () => {
            try {
                const postsData = await fetchPosts();
                const postsWithVotes = await Promise.all(postsData.map(async (post) => {
                    const votesCount = await fetchPostVotes(post._id);
                    return { ...post, upvotes: votesCount, upvoted: false, downvoted: false };
                }));
                setPosts(postsWithVotes);
            } catch (error) {
                console.error('Error loading posts:', error);
                setPosts([]);
            }
        };
        initFetch();
    }, []);

    const handleVote = async (postId: string, type: 'upvote' | 'downvote' | 'undo') => {
        try {
            const updatedPosts = posts.map(post => {
                if (post._id === postId) {
                    if (type === 'upvote') return { ...post, upvotes: post.upvotes + 1, upvoted: true };
                    if (type === 'downvote') return { ...post, upvotes: post.upvotes - 1, downvoted: true };
                    if (type === 'undo') return { ...post, upvoted: false, downvoted: false };
                }
                return post;
            });
            setPosts(updatedPosts);

            if (type === 'upvote') await upvotePost({ postId, userId: 'YOUR_USER_ID' });
            if (type === 'downvote') await downvotePost({ postId, userId: 'YOUR_USER_ID' });
            if (type === 'undo') await undoVotePost({ postId, userId: 'YOUR_USER_ID' });
        } catch (error) {
            console.error(`Error ${type} post:`, error);
        }
    };

    return (
        <div className="posts-wrapper">
            {posts.map((post) => (
                <div key={post._id} className="post">
                    <div className="post-sidebar">
                        <ThumbUpOffAltIcon className="upvote" onClick={() => handleVote(post._id, post.upvoted ? 'undo' : 'upvote')} />
                        <span>{post.upvotes}</span>
                        <ThumbDownOffAltIcon className="downvote" onClick={() => handleVote(post._id, post.downvoted ? 'undo' : 'downvote')} />
                    </div>
                    <div className="post-title">
                        <span>Posted by {post._creator.username}</span>
                        <div className="post-title-username">{post.title}</div>
                        <div className="post-title-time">{new Date(post.createdAt).toLocaleString()}</div>
                        <Button label="+Follow" className="follow-button" />
                    </div>
                    <div className="post-body">{post.text}</div>
                    <div className="post-footer">
                        <ModeComment />
                        <span>{post._comments.length} Comments</span>
                        <Share />
                        <span>Share</span>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Posts;
