import { upvotePost, downvotePost, undoVotePost } from '../../../api/api';
import { Post } from '../../../models/post/PostModel';

type VoteType = 'upvote' | 'downvote' | 'undo';

export const handleVote = async (
    posts: Post[],
    setPosts: React.Dispatch<React.SetStateAction<Post[]>>,
    postId: string,
    type: VoteType,
): Promise<void> => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (!user || !user._id) {
        alert("You need to be logged in to vote.");
        return;
    }

    const userId = user._id;
    const postIndex = posts.findIndex(post => post._id === postId);
    if (postIndex === -1) {
        alert("Post not found.");
        return;
    }

    let currentPost = posts[postIndex];
    try {
        const response = await (type === 'upvote' ? upvotePost : type === 'downvote' ? downvotePost : undoVotePost)({
            postId,
            userId
        });

        if (response.success) {
            // Update the upvotes in place
            currentPost.upvotes = response.netVotes ?? currentPost.upvotes;
            // Create a new array with updated post data for immutability
            const updatedPosts = [...posts];
            updatedPosts[postIndex] = currentPost;
            setPosts(updatedPosts);
        } else {
            alert(response.message);
        }
    } catch (error) {
        alert("Failed to execute vote operation.");
    }
};
