import React, { useState } from 'react';
import './Actions.css';
import Button from "../../button/Button"; // Make sure the path is correct
import PersonIcon from '@mui/icons-material/Person';
import { ArrowDropDown } from "@mui/icons-material";


// Define a test user data
const TEST_USER = {
    id: "12345",
    username: "testuser",
    email: "testuser@example.com"
};

interface User {
    id: string;
    username: string;
    email: string;
}

export default function Actions() {
    const [user, setUser] = useState<User | null>(null);

    const handleLogin = () => {
        setUser({
            id: "12345",
            username: "testuser",
            email: "testuser@example.com"
        }); // This now matches the User type
        localStorage.setItem('user', JSON.stringify(TEST_USER));
        console.log('Logged in as Test User:', TEST_USER);
    };

    const handleLogout = () => {
        setUser(null);
        localStorage.removeItem('user');
    };

    return (
        <div className="actions">
            <div className={user ? "hidden" : "visible"}>
                <Button label="Log In" onClick={handleLogin} />
            </div>
            <div className={user ? "profile visible" : "profile hidden"}>
                {user && (
                    <>
                        <PersonIcon className="hoverable" />
                        <span>{user.username}</span>
                        <ArrowDropDown className="hoverable" />
                        <Button label="Log Out" onClick={handleLogout} />
                    </>
                )}
            </div>
        </div>
    );
}
