import React, { useState, useEffect } from 'react';
import { fetchPosts, fetchPostVotes, upvotePost, downvotePost, undoVotePost, postComment, getCommentsForPost } from '../../../api/api';
import './Posts.css';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import Button from '../../button/Button';
import { ModeComment, Share } from "@mui/icons-material";
import { Post } from '../../../models/post/PostModel';
import CommentsSection from "../comments/CommentsSection";  // Ensure the correct path
import { handleVote } from './handleVote';

interface PostsProps {
    currentFilter: string;
}

const Posts: React.FC<PostsProps> = ({ currentFilter }) => {
    const [posts, setPosts] = useState<Post[]>([]);
    const userId = 'YOUR_USER_ID';  // This should be dynamically set based on logged-in user info

    useEffect(() => {
        const initFetch = async () => {
            const data = await fetchPosts();
            const postsWithVotes = await Promise.all(data.map(async post => {
                const votesCount = await fetchPostVotes(post._id);
                return {
                    ...post,
                    upvotes: votesCount,
                    upvoted: false,
                    downvoted: false,
                    _comments: [],
                    showComments: false
                };
            }));
            setPosts(postsWithVotes);
        };

        initFetch();
    }, [currentFilter]);

    const handleCommentIconClick = async (postId: string) => {
        const postIndex = posts.findIndex(post => post._id === postId);
        if (postIndex === -1) return;

        const currentPosts = [...posts];
        const currentPost = currentPosts[postIndex];
        if (!currentPost.showComments && currentPost._comments.length === 0) {
            // Load comments only if they have not been loaded yet
            const comments = await getCommentsForPost(postId);
            currentPost._comments = comments;
        }
        currentPost.showComments = !currentPost.showComments;
        setPosts(currentPosts);
    };

    const handleAddComment = async (postId: string, newCommentText: string) => {
        if (!newCommentText.trim()) return;
        try {
            const newComment = await postComment(newCommentText, postId, userId);
            const updatedPosts = posts.map(post =>
                post._id === postId ? { ...post, _comments: [...post._comments, newComment], showComments: true } : post
            );
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error posting comment:', error);
        }
    };

    return (
        <div className="posts-wrapper">
            {posts.map(post => (
                <div key={post._id} className="post">
                    <div className="post-sidebar">
                        <ThumbUpOffAltIcon className="upvote" onClick={() => handleVote(posts, setPosts, post._id, 'upvote', userId)} />
                        <span>{post.upvotes}</span>
                        <ThumbDownOffAltIcon className="downvote" onClick={() => handleVote(posts, setPosts, post._id, 'downvote', userId)} />
                    </div>
                    <div className="post-title">
                        <span>Posted by {post._creator.username}</span>
                        <div className="post-title-username">{post.title}</div>
                        <div className="post-title-time">{new Date(post.createdAt).toLocaleString()}</div>
                        <Button label="+ Follow" className="follow-button" />
                    </div>
                    <div className="post-body">{post.text}</div>
                    <div className="post-footer">
                        <ModeComment onClick={() => handleCommentIconClick(post._id)} />
                        <span>{post._comments.length} Comments</span>
                        <Share />
                        <span>Share</span>
                    </div>
                    {post.showComments && (
                        <CommentsSection postId={post._id} />
                    )}
                </div>
            ))}
        </div>
    );
};

export default Posts;
