import { Post } from '../../../models/post/PostModel';
import { upvotePost, downvotePost, undoVotePost } from '../../../api/api';

type VoteType = 'upvote' | 'downvote' | 'undo';

export const handleVote = async (
    posts: Post[],
    setPosts: React.Dispatch<React.SetStateAction<Post[]>>,
    postId: string,
    type: VoteType,
    userId: string
): Promise<void> => {
    const postIndex = posts.findIndex(post => post._id === postId);
    if (postIndex === -1) {
        console.error("Post not found for the given ID:", postId);
        return;
    }

    let updatedPosts = [...posts];
    let currentPost = updatedPosts[postIndex];

    try {
        const response = await (type === 'upvote' ? upvotePost : type === 'downvote' ? downvotePost : undoVotePost)({
            postId, userId
        });

        if (response.success) {
            // Assuming netVotes is returned correctly from the backend
            currentPost.upvotes = response.netVotes ?? currentPost.upvotes;
            currentPost.upvoted = type === 'upvote' && response.netVotes !== undefined;
            currentPost.downvoted = type === 'downvote' && response.netVotes !== undefined;
            updatedPosts[postIndex] = currentPost;
            setPosts(updatedPosts);
        } else {
            console.error("API error response:", response.message);
        }
    } catch (error) {
        console.error("Failed to execute vote operation:", error);
    }
};
