import React from 'react';
import './Actions.css';
import Button from "../../button/Button"; // Make sure the path is correct
import PersonIcon from '@mui/icons-material/Person';
import { ArrowDropDown } from "@mui/icons-material";

// Define a test user data
const TEST_USER = {
    id: "12345",
    username: "testuser",
    email: "testuser@example.com"
};

export default function Actions() {
    // Function to simulate user login
    const handleLogin = () => {
        // Here, implement what happens after login, such as setting global state or localStorage
        localStorage.setItem('user', JSON.stringify(TEST_USER));  // Storing user info in localStorage for simplicity
        console.log('Logged in as Test User:', TEST_USER);
        // Add any redirection or additional actions needed after login
    };

    return (
        <div className="actions">
            <Button label="Log In" onClick={handleLogin}/> {/* Attach the handleLogin function */}
            <div className="profile">
                <PersonIcon className="hoverable"/>
                <ArrowDropDown className="hoverable"/>
            </div>
        </div>
    );
}
