import React, { useEffect, useState } from 'react';
import { fetchPosts, fetchPostVotes, upvotePost, downvotePost, undoVotePost } from '../../../api/api'; // Import necessary API functions
import './Posts.css';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import Button from '../../button/Button';
import { ModeComment, Share } from "@mui/icons-material";

// Adjust your Post interface to include all necessary properties
interface Post {
    _id: string;
    title: string;
    text: string;
    link?: string;
    userId: string;
    _comments: Comment[]; // Assuming you have a Comment interface
    upvotes: number;
    _creator: { username: string };
    createdAt: string;
    updatedAt?: string;
}

// Assuming PostData is structured as returned by the API
interface PostData {
    _id: string;
    title: string;
    text: string;
    link?: string;
    userId: string;
    _creator: { username: string };
    createdAt: string;
    updatedAt?: string;
}

const Posts: React.FC = () => {
    const [posts, setPosts] = useState<Post[]>([]);

    useEffect(() => {
        const initFetch = async () => {
            try {
                const data: PostData[] = await fetchPosts();

                // Fetch and update upvotes count for each post
                const postsWithVotes: Promise<Post>[] = data.map(async (post) => {
                    const votesCount = await fetchPostVotes(post._id);
                    return {
                        ...post,
                        _comments: [], // Default empty array for comments
                        upvotes: votesCount, // Set upvotes to fetched votes count
                    };
                });

                // Wait for all promises to resolve
                const enrichedPosts: Post[] = await Promise.all(postsWithVotes);

                setPosts(enrichedPosts);
            } catch (error) {
                console.error('Error loading posts:', error);
                setPosts([]);
            }
        };

        initFetch();
    }, []);

    const handleUpvote = async (postId: string) => {
        try {
            await upvotePost({ postId, userId: 'user_id_here' }); // Pass the user ID
            const updatedPosts = posts.map(post =>
                post._id === postId ? { ...post, upvotes: post.upvotes + 1 } : post
            );
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error upvoting post:', error);
        }
    };

    const handleDownvote = async (postId: string) => {
        try {
            await downvotePost({ postId, userId: 'user_id_here' }); // Pass the user ID
            const updatedPosts = posts.map(post =>
                post._id === postId ? { ...post, upvotes: post.upvotes - 1 } : post
            );
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error downvoting post:', error);
        }
    };

    const handleUndoVote = async (postId: string) => {
        try {
            await undoVotePost({ postId, userId: 'user_id_here' }); // Pass the user ID
            const updatedPosts = posts.map(post =>
                post._id === postId ? { ...post, upvotes: post.upvotes } : post
            );
            setPosts(updatedPosts);
        } catch (error) {
            console.error('Error undoing vote on post:', error);
        }
    };

    return (
        <div className="posts-wrapper">
            {posts.map((post: Post) => (
                <div key={post._id} className="post">
                    <div className="post-sidebar">
                        <ThumbUpOffAltIcon className="upvote" onClick={() => handleUpvote(post._id)} />
                        <span>{post.upvotes}</span>
                        <ThumbDownOffAltIcon className="downvote" onClick={() => handleDownvote(post._id)} />
                    </div>
                    <div className="post-title">
                        <span>Posted by {post._creator.username}</span>
                        <div className="post-title-username">{post.title}</div>
                        <div className="post-title-time">{new Date(post.createdAt).toLocaleString()}</div>
                        <Button label="+Follow" className="follow-button" />
                    </div>
                    <div className="post-body">{post.text}</div>
                    <div className="post-footer">
                        <div className="comments footer-action">
                            <ModeComment />
                            <span>{post._comments.length}</span>
                        </div>
                        <div className="share footer-action">
                            <Share />
                            <span>Share</span>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Posts;
