import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from "../../navbar/Navbar";
import './CreatePost.css'; // Import CSS file for styling
import { AuthContext } from "../../context/AuthContext"; // Import your authentication context

const CreatePost: React.FC = () => {
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [link, setLink] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();
    const { user } = useContext(AuthContext); // Access user info from context

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        // Validate form fields
        if (!title || !text || !link) {
            setError('All fields are required');
            return;
        }

        // Assume you handle the post creation here
        console.log({ title, text, link, userId: user.id }); // Use user ID from context

        // Reset error message
        setError('');

        // Navigate back to the landing page after post creation
        navigate('/');
    };

    return (
        <div>
            <Navbar />
            <form className="create-post-form" onSubmit={handleSubmit}>
                <label>
                    Title:
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </label>
                <label>
                    Text:
                    <textarea value={text} onChange={(e) => setText(e.target.value)} />
                </label>
                <label>
                    Link:
                    <input type="text" value={link} onChange={(e) => setLink(e.target.value)} />
                </label>
                {error && <p className="error">{error}</p>}
                <button type="submit" className="create-post-button">Create Post</button>
            </form>
        </div>
    );
};

export default CreatePost;
