import axios from 'axios';

// Set up the base URL for all API requests
const API = axios.create({
    baseURL: process.env.REACT_APP_API_URL // Ensure you have REACT_APP_API_URL in your .env file
});

// Function to fetch posts
export const fetchPosts = async () => {
    try {
        const response = await API.get('/posts');
        return response.data; // Returns the data from the API
    } catch (error) {
        throw error;
    }
};

// Function to create a post
export const createPost = async (postData: { title: string; content: string }) => {
    try {
        const response = await API.post('/posts', postData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

