import React, { useState } from 'react';
import './Actions.css';
import Button from "../../button/Button"; // Make sure the path is correct
import PersonIcon from '@mui/icons-material/Person';


interface User {
    id: string;
    username: string;
    email: string;
}

export default function Actions() {
    const [user, setUser] = useState<User | null>(null);

    const handleLogin = () => {
        // Read user data from localStorage if available
        const loggedInUser = localStorage.getItem('user');
        if (loggedInUser) {
            const parsedUser: User = JSON.parse(loggedInUser);
            setUser(parsedUser);
            console.log('Logged in as User:', parsedUser);
        } else {
            console.log('No user data found.');
        }
    };

    const handleLogout = () => {
        setUser(null);
        localStorage.removeItem('user');
        console.log('Logged out');
    };

    return (
        <div className="actions">
            {!user && <Button label="Log In" onClick={handleLogin} />}
            {user && (
                <div className="profile">
                    <PersonIcon className="hoverable" />
                    <span>{user.username}</span> {/* This is now valid */}

                    <Button label="Log Out" onClick={handleLogout} />
                </div>
            )}
        </div>
    );
}
