import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from "../../navbar/Navbar";
import './CreatePost.css';

const CreatePost: React.FC = () => {
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [link, setLink] = useState('');
    const userId = "12345";
    const [error, setError] = useState('');
    const [user, setUser] = useState<{ id: string } | null>(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Check if user is logged in and is the test user
        const loggedUser = localStorage.getItem('user');
        if (loggedUser) {
            const userInfo = JSON.parse(loggedUser);
            if (userInfo.id === userId) {
                setUser(userInfo);
            } else {
                setError('You are not authorized to post.');
            }
        } else {
            setError('You must be logged in to post.');
        }
    }, []);

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        if (!user) {
            setError('You must be logged in as the test user to post.');
            return;
        }

        if (!title || !text || !link) {
            setError('All fields are required');
            return;
        }

        console.log({ title, text, link, userId: user.id });
        setError('');
        navigate('/');
    };

    const handleCancel = () => {
        navigate('/');
    };

    return (
        <div>
            <Navbar />
            <form className="create-post-form" onSubmit={handleSubmit}>
                <label>
                    Title:
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </label>
                <label>
                    Text:
                    <textarea value={text} onChange={(e) => setText(e.target.value)} />
                </label>
                <label>
                    Link:
                    <input type="text" value={link} onChange={(e) => setLink(e.target.value)} />
                </label>
                {error && <p className="error">{error}</p>}
                <div className="create-post-button-container">
                    <button type="submit" className="create-post-button" disabled={!user}>Create Post</button>
                    <button type="button" className="create-post-button" onClick={handleCancel}>Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default CreatePost;
